 Copy / Clone directory and files
 source  KyleMit
javascript
const { promises: fs }  require"fs"
const path  require"path"

async function copyDirsrc, dest {
    await fs.mkdirdest, { recursive: true };
    let entries  await fs.readdirsrc, { withFileTypes: true };

    for let entry of entries {
        let srcPath  path.joinsrc, entry.name;
        let destPath  path.joindest, entry.name;

        entry.isDirectory ?
            await copyDirsrcPath, destPath :
            await fs.copyFilesrcPath, destPath;
    }
}


